function set_default_choice(){
	var icon_name;
	if(flag && flag.length > 0 && wan_route_x != "IP_Bridged"){
		if(flag == "Internet")
			$("statusframe").src = "/device-map/internet.asp";
		else if(flag == "Client")
			$("statusframe").src = "/device-map/clients.asp";
		else{
			clickEvent($("iconRouter"));
			return;
		}

		icon_name = "icon"+flag;
		clickEvent($(icon_name));
	}
	else
		clickEvent($("iconRouter"));
}


function showMapWANStatus(flag){

	if(flag == 1){
		showtext($("internetStatus"), "\xE5\xB7\xB2\xE9\x80\xA3\xE7\xB7\x9A");
		$("ifconnect").style.display = "none";
	}
	else{
		showtext($("internetStatus"), "\xE9\x80\xA3\xE7\xB7\x9A\xE4\xB8\xAD\xE6\x96\xB7");
		$("ifconnect").style.display = "block";
	}
}

function show_middle_status(){
	var auth_mode = document.form.wl_auth_mode.value;
	var wpa_mode = document.form.wl_wpa_mode.value;
	var wl_wep_x = parseInt(document.form.wl_wep_x.value);
	var crypto_code = document.form.wl_crypto.value;
	var key_mode;
	var security_mode;

	//2010.08.24 Vanic modify

	if(auth_mode == "open"){
		if(wl_wep_x == 0){
			$("iflock").style.background = 'url(images/unlock_icon.gif) no-repeat';
			security_mode = "Open System";
			key_mode = "None";
		}
		else if(wl_wep_x == 1){
			security_mode = "Open System";
			key_mode = "WEP:64bits";
		}
		else if(wl_wep_x == 2){
			security_mode = "Open System";
			key_mode = "WEP:128bits";
		}
	}
	else if(auth_mode == "shared"){
		security_mode = "Shared Key";
		if(wl_wep_x == 1){
			key_mode = "WEP:64bits";
		}
		else if(wl_wep_x == 2){
			key_mode = "WEP:128bits";
		}
	}
	else if(auth_mode == "psk" || auth_mode == "psk2"){
		// justify key mode
		if(crypto_code == "aes"){key_mode ="AES";}
		else if(crypto_code == "tkip"){key_mode ="TKIP";}
		else if(crypto_code == "tkip+aes"){key_mode ="TKIP+AES";}
		else if(crypto_code == ""){key_mode ="";}
		// END
		if(wpa_mode == "1")
			security_mode = "WPA-Personal";
		else if(wpa_mode == "2")
			security_mode = "WPA2-Personal";
		else if(wpa_mode == "0")
			security_mode = "WPA-Auto-Personal";
		else
			alert("System error for showing auth_mode!");
	}
	else if(auth_mode == "wpa"){
		// justify key mode
		if(crypto_code == "aes"){key_mode ="AES";}
		else if(crypto_code == "tkip"){key_mode ="TKIP";}
		else if(crypto_code == "tkip+aes"){key_mode ="TKIP+AES";}
		else if(crypto_code == ""){key_mode ="";}
		// END
		if(wpa_mode == "3")
			security_mode = "WPA-Enterprise";
		else if(wpa_mode == "4")
			security_mode = "WPA-Auto-Enterprise";
		else
			alert("System error for showing auth_mode!");
	}
	else if(auth_mode == "wpa2"){
		security_mode = "WPA2-Enterprise";
		// justify key mode
		if(crypto_code == "aes"){key_mode ="AES";}
		else if(crypto_code == "tkip"){key_mode ="TKIP";}
		else if(crypto_code == "tkip+aes"){key_mode ="TKIP+AES";}
		else if(crypto_code == ""){key_mode ="";}
		// END
	}
	else if(auth_mode == "radius")
		security_mode = "Radius with 802.1x";
	else
		security_mode = "Unknown Auth";


	$("wl_securitylevel_span").innerHTML = security_mode; // show security mode in page
	$("wl_securitylevel_key_mode").innerHTML = key_mode ; // show key mode in page

	$("iflock").style.display = "block";

	// clients
	show_client_status();			
}


function show_client_status(){
	var client_str = "";
	var wired_num = 0, wireless_num = 0;

	if(sw_mode == "1"){
		client_str += "\xE7\x94\xA8\xE6\x88\xB6\xE6\x95\xB8: <span>"+clients.length+"</span>";
	}
	else		
		client_str += "\xE7\x94\xA8\xE6\x88\xB6\xE7\x8B\x80\xE6\x85\x8B\xE9\x97\x9C\xE9\x96\x89";

	$("clientNumber").innerHTML = client_str;
}

function show_device(){
	var usb_path1 = '';
	//var usb_path1 = '';	
	// show the front usb device

	switch(usb_path1){
		case "storage":
			for(var i = 0; i < all_disks.length; ++i){
				if(all_disk_interface[i] == "USB"){
					disk_html(0, i);
					break;
				}
			}
			break;
		case "audio":
			break;
		case "printer":
			printer_html(0, 0);
			break;
		case "HSDPA":
			HSDPA_html(0, 0);
			break;
		case "webcam":
			break;
		default:
			no_device_html(0);
	}
}

