function clickEvent(obj){
	var icon;
	var ContainerWidth;
	var stitle;

	if(obj.id == "iflock"){
		obj = $("iconRouter");
	}

	if(obj.id.indexOf("Internet") > 0){
		icon = "iconInternet";
		ContainerWidth = "300px";
		stitle = "\xE7\xB6\xB2\xE9\x9A\x9B\xE7\xB6\xB2\xE8\xB7\xAF\xE7\x8B\x80\xE6\x85\x8B";
		$("statusframe").src = "/device-map/internet.asp";
	}
	else if(obj.id.indexOf("Router") > 0){
		icon = "iconRouter";
		ContainerWidth = "340px";
		stitle = "ASUS RT-N10E";
	}
	else if(obj.id.indexOf("Client") > 0){
		if(sw_mode != "1")
			return;

		icon = "iconClient";
		ContainerWidth = "380px";
		stitle = "\xE7\x94\xA8\xE6\x88\xB6\xE7\xAB\xAF\xE7\x8B\x80\xE6\x85\x8B";
	}
	else if(obj.id.indexOf("USBdisk") > 0){
		icon = "iconUSBdisk";
		ContainerWidth = "300px";
		stitle = "\xE5\xA4\x96\xE6\x8E\xA5USB\xE7\xA3\x81\xE7\xA2\x9F\xE7\x8B\x80\xE6\x85\x8B";
		$("statusframe").src = "/device-map/disk.asp";
	}
	else if(obj.id.indexOf("Printer") > 0){
		icon = "iconPrinter";
		ContainerWidth = "300px";
		stitle = "\xE5\x8D\xB0\xE8\xA1\xA8\xE6\xA9\x9F\xE7\x8B\x80\xE6\x85\x8B";
	}
	else if(obj.id.indexOf("Remote") > 0){
		if(sw_mode == "2")
			return;

		icon = "iconRemote";
		ContainerWidth = "300px";
		stitle = "\xE9\x81\xA0\xE7\xAB\xAF\xE5\x9F\xBA\xE5\x9C\xB0\xE5\x8F\xB0\xE7\x8B\x80\xE6\x85\x8B";
		$("statusframe").src = "";
		//$("statusframe").src = "/device-map/remote.asp";
		//alert($("statusframe").src);
		//alert(obj.id);
	}	
	else if(obj.id.indexOf("No") > 0){
		icon = "iconNo";
	}
	else
		alert("mouse over on wrong place!");

	$('statusContainer').style.width = ContainerWidth;
	if(lastClicked){
		lastClicked.style.background = 'url(images/map-'+lastName+'.gif) no-repeat';
	}

	obj.style.background = 'url(images/map-'+icon+'_d.gif) no-repeat';
	$('statusIcon').src = 'images/iframe-'+icon+'.gif';

	$('helpname').innerHTML = stitle;

	avoidkey = icon;
	lastClicked = obj;
	lastName = icon;
}

function mouseEvent(obj, key){
	var icon;

	if(obj.id.indexOf("Internet") > 0)
		icon = "iconInternet";
	else if(obj.id.indexOf("Router") > 0)
		icon = "iconRouter";
	else if(obj.id.indexOf("Client") > 0){
		if(wan_route_x == "IP_Bridged")
			return;

		icon = "iconClient";
	}
	else if(obj.id.indexOf("USBdisk") > 0)
		icon = "iconUSBdisk";
	else if(obj.id.indexOf("Printer") > 0)
		icon = "iconPrinter";
	else if(obj.id.indexOf("No") > 0)
		icon = "iconNo";
	else
		alert("mouse over on wrong place!");

	if(avoidkey != icon){
		if(key){ //when mouseover
			obj.style.background = 'url("/images/map-'+icon+'_r.gif") no-repeat';
		}
		else {  //when mouseout
			obj.style.background = 'url("/images/map-'+icon+'.gif") no-repeat';
		}
	}
}//end of mouseEvent

