var $j = jQuery.noConflict();
var aplist = "";

function getConnectingStatus(){
	if(aplist){
		for(var i=0; i<aplist.length ;i++){
			if(aplist[i][8] != 0){
				return aplist[i][8]; 
			}
			else{
				continue;
			}
		}
		return 0;
	}
	return -1;
}

function ajaxGetData(e) {	
	if(URE_CONN_STATUS=="1"){
			$j("#internetStatus").html("\xE5\xB7\xB2\xE9\x80\xA3\xE7\xB7\x9A");
			getConnectingStatus();

	}else if(URE_CONN_STATUS=="0"){
			$j("#internetStatus").html("\xE6\x9C\xAA\xE9\x80\xA3\xE7\xB7\x9A");
			getConnectingStatus();		
	}	else{
  	$j.ajax({
    	url: 'aplist.asp',
    	dataType: 'script', 

    	error: function(xhr) {      	;},
    	success: function(response) {
	    	if(getConnectingStatus()==2 || getConnectingStatus()==4){
	    		$j("#internetStatus").html("\xE5\xB7\xB2\xE9\x80\xA3\xE7\xB7\x9A");
					//$("wl_radio_obj").style.visibility = "visible";
	    	}
	  		else{
	    		$j("#internetStatus").html("\xE6\x9C\xAA\xE9\x80\xA3\xE7\xB7\x9A");
					//$("wl_radio_obj").style.visibility = "hidden";
	  		}
    	}
  	});
	}
}

$j(document).ready(function($){
	if(sw_mode == "2"){
		setTimeout("ajaxGetData();",2000);
	}
});

